#!/usr/bin/env pythonclass Solution:    # @return a list of lists of length 3, [[val1,val2,val3]]    def threeSum(self, num):        if not num or len(num) < 3:            return []        num.sort() # sort the sequence in-place        result = []        for i in xrange(len(num)):            if i-1 >= 0 and num[i-1] == num[i]:                continue            lo, hi = i+1, len(num)-1            while lo < hi:                sol = [num[i], num[lo], num[hi]]                sums = sum(sol)                if sums == 0:                    result.append(sol)                    lo, hi = lo+1, hi-1                    while lo < hi and num[lo] == num[lo-1]:                        lo += 1                    while lo < hi and num[hi] == num[hi+1]:                        hi -= 1                elif sums < 0:                    lo += 1                else:                    hi -= 1        return result